import * as React from "react"
import * as AccordionPrimitive from "@radix-ui/react-accordion"
import { ChevronDown } from "lucide-react"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

const baseBrutalStyle = [
  "border-2 border-black",
  "shadow-[4px_4px_0_#000]",
].join(" ")

const accordionVariants = cva("w-full", {
  variants: {
    variant: {
      brutal: "",
      regular: "",
      outline: "",
    },
  },
  defaultVariants: {
    variant: "brutal",
  },
})

const accordionItemVariants = cva(
  "overflow-hidden transition-all",
  {
    variants: {
      variant: {
        brutal: `bg-[var(--primary)] ${baseBrutalStyle} first:rounded-t-lg last:rounded-b-lg [&:not(:last-child)]:border-b-0`,
        regular: `bg-white ${baseBrutalStyle} first:rounded-t-lg last:rounded-b-lg [&:not(:last-child)]:border-b-0`,
        outline: "border-b border-border last:border-b-0",
      },
    },
    defaultVariants: {
      variant: "brutal",
    },
  }
)

const accordionTriggerVariants = cva(
  "flex flex-1 items-center justify-between py-4 px-5 text-left font-semibold transition-all [&[data-state=open]>svg]:rotate-180",
  {
    variants: {
      variant: {
        brutal: "hover:bg-black/5 text-black",
        regular: "hover:bg-black/5 text-black",
        outline: "hover:underline",
      },
    },
    defaultVariants: {
      variant: "brutal",
    },
  }
)

const accordionContentVariants = cva(
  "overflow-hidden text-sm data-[state=closed]:animate-accordion-up data-[state=open]:animate-accordion-down",
  {
    variants: {
      variant: {
        brutal: "bg-white/50 border-t-2 border-black",
        regular: "bg-gray-50 border-t-2 border-black",
        outline: "border-t border-border",
      },
    },
    defaultVariants: {
      variant: "brutal",
    },
  }
)

type AccordionVariant = "brutal" | "regular" | "outline"

interface AccordionContextValue {
  variant: AccordionVariant
}

const AccordionContext = React.createContext<AccordionContextValue>({
  variant: "brutal",
})

type NeoAccordionProps = React.ComponentProps<typeof AccordionPrimitive.Root> &
  VariantProps<typeof accordionVariants>

function NeoAccordion({
  className,
  variant = "brutal",
  ...props
}: NeoAccordionProps) {
  return (
    <AccordionContext.Provider value={{ variant: variant ?? "brutal" }}>
      <AccordionPrimitive.Root
        data-slot="accordion"
        data-variant={variant}
        className={cn(accordionVariants({ variant, className }))}
        {...props}
      />
    </AccordionContext.Provider>
  )
}

type NeoAccordionItemProps = React.ComponentProps<typeof AccordionPrimitive.Item>

function NeoAccordionItem({ className, ...props }: NeoAccordionItemProps) {
  const { variant } = React.useContext(AccordionContext)

  return (
    <AccordionPrimitive.Item
      data-slot="accordion-item"
      className={cn(accordionItemVariants({ variant, className }))}
      {...props}
    />
  )
}

type NeoAccordionTriggerProps = React.ComponentProps<typeof AccordionPrimitive.Trigger>

function NeoAccordionTrigger({
  className,
  children,
  ...props
}: NeoAccordionTriggerProps) {
  const { variant } = React.useContext(AccordionContext)

  return (
    <AccordionPrimitive.Header className="flex">
      <AccordionPrimitive.Trigger
        data-slot="accordion-trigger"
        className={cn(accordionTriggerVariants({ variant, className }))}
        {...props}
      >
        {children}
        <ChevronDown className="size-5 shrink-0 transition-transform duration-200" />
      </AccordionPrimitive.Trigger>
    </AccordionPrimitive.Header>
  )
}

type NeoAccordionContentProps = React.ComponentProps<typeof AccordionPrimitive.Content>

function NeoAccordionContent({
  className,
  children,
  ...props
}: NeoAccordionContentProps) {
  const { variant } = React.useContext(AccordionContext)

  return (
    <AccordionPrimitive.Content
      data-slot="accordion-content"
      className={cn(accordionContentVariants({ variant, className }))}
      {...props}
    >
      <div className="px-5 py-4">{children}</div>
    </AccordionPrimitive.Content>
  )
}

export {
  NeoAccordion,
  NeoAccordionItem,
  NeoAccordionTrigger,
  NeoAccordionContent,
}

export type {
  NeoAccordionProps,
  NeoAccordionItemProps,
  NeoAccordionTriggerProps,
  NeoAccordionContentProps,
}

export const variantOptions = [
  { variant: "brutal", label: "Brutal" },
  { variant: "regular", label: "Regular" },
  { variant: "outline", label: "Outline" },
] as const
