import * as React from "react"
import { cva, type VariantProps } from "class-variance-authority"
import { cn } from "../../../lib/utils"

const neoButtonVariants = cva(
  "inline-flex items-center justify-center whitespace-nowrap rounded font-bold transition-all active:translate-y-[2px] active:shadow-none disabled:opacity-50 disabled:cursor-not-allowed focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-offset-2",
  {
    variants: {
      variant: {
        primary:
          "border-4 border-black bg-white text-black hover:bg-black hover:text-white hover:border-white shadow-[4px_4px_0px_0px_rgba(0,0,0,1)]",
        secondary:
          "border-4 border-black bg-black text-white hover:bg-white hover:text-black hover:border-black shadow-[4px_4px_0px_0px_rgba(0,0,0,1)]",
        accent:
          "border-4 border-white bg-black text-white hover:bg-white hover:text-black hover:border-black shadow-[4px_4px_0px_0px_rgba(255,255,255,1)]",
        outline:
          "border-4 border-black bg-transparent text-black hover:bg-black hover:text-white",
        danger:
          "border-4 border-red-600 bg-red-600 text-white hover:bg-white hover:text-red-600 hover:border-red-600 shadow-[4px_4px_0px_0px_rgba(220,38,38,1)]",
        ghost:
          "border-3 border-black bg-transparent text-black hover:bg-black hover:text-white",
      },
      size: {
        sm: "h-8 px-3 text-sm",
        md: "h-10 px-4 text-base",
        lg: "h-12 px-6 text-lg",
        xl: "h-14 px-8 text-xl",
      },
    },
    defaultVariants: {
      variant: "primary",
      size: "md",
    },
  }
)

export interface NeoButtonProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement>,
    VariantProps<typeof neoButtonVariants> {}

const NeoButton = React.forwardRef<HTMLButtonElement, NeoButtonProps>(
  ({ className, variant, size, ...props }, ref) => (
    <button
      ref={ref}
      className={cn(neoButtonVariants({ variant, size, className }))}
      {...props}
    />
  )
)
NeoButton.displayName = "NeoButton"

export { NeoButton, neoButtonVariants }
